jQuery('div.wrapper').ready( function() {
	var elementoCkanWrapper = jQuery('div.wrapper');

	if (elementoCkanWrapper) {
		var elementoCkanPrimary = elementoCkanWrapper.find('div.primary');
		var alturaWrapper = elementoCkanWrapper.height();

		if (elementoCkanPrimary) {
			elementoCkanPrimary.css('height', alturaWrapper + 'px');
			elementoCkanPrimary.css('overflow', 'auto');
		}
	}
});


